mapper.select:
select a.id,a.name,u.name owner,start_date,end_date,us.name create_by,a.cost,a.description,a.create_time, u3.name edit_by,a.edit_time edit_time from tbl_activity a inner join tbl_user u on u.id = a.owner inner join tbl_user us on us.id = a.create_by left join tbl_user u3 on u3.id = a.edit_by
and a.name like '%${name}%'
and u.name like '%${owner}%'
and a.start_date >= #{startDate}
and a.end_date <= #{endDate}
order by a.create_time desc limit #{beginNo},#{pageSize}

select count(0) from tbl_activity a inner join tbl_user u on u.id = a.owner inner join tbl_user us on us.id = a.create_by left join tbl_user u3 on u3.id = a.edit_by
and a.name like '%${name}%'
and u.name like '%${owner}%'
and a.start_date >= #{startDate}
and a.end_date <= #{endDate}

mapper.update:
update tbl_activity set
      name = #{name} , owner = #{owner} ,
      start_date = #{startDate} , end_date = #{endDate} ,
      cost = #{cost} , description = #{description} ,
      edit_time = #{editTime} , edit_by = #{editBy}
where id = #{id}

mapper.select:
select a.id , a.name , u.name owner , a.start_date , a.end_date from tbl_activity a inner join tbl_user u on u.id = a.owner inner join tbl_clue_activity_relation car on car.activity_id = a.id
and a.name like '%${activityName}%'
and car.clue_id = #{clueId}
